mapper.select:

SELECT COUNT(*)
FROM USER_TBL 
WHERE USER_ID = #{userId} AND USER_NAME = #{userName} 
AND REPLACE(PHONE, '-', '') = #{phone}

mapper.update:
UPDATE USER_TBL
SET USER_PASS = #{hashPwd}
WHERE USER_ID = #{userId}

mapper.select:

SELECT USER_ID 
FROM USER_TBL
WHERE USER_NAME = #{userName} AND PHONE = #{phone}
